<template>
  <section class="call-page" :class="[isDialing ? 'is-dialing' : 'is-answered']">
    <section class="background">
      <img src="/assets/images/call_background.png">
    </section>
    <section class="body no-scrollbar" :class="[persons > 1 ? 'multi-voice-call' : '', addCall ? 'add-call' : '']">
