<script setup>
import AddUser from "@/components/Chat/VoiceCall/AddUser";
definePageMeta({
  layout: 'full'
})

const isDialing = ref(true)
const persons = ref(1)
const timer = ref(0)
const interval = ref('')
const addCall = ref(false)
const actionButtonsStatus = reactive({
  voice: true,
})

// methods
const toggleAddCall = () => {
  addCall.value = !addCall.value
}
const selectUser = () => {
  toggleAddCall()
  if (persons.value < 5) {
    persons.value++
  }
}

// watcher
watch(isDialing, (value) => {
  if (value == false) {
    timer.value = 0
    interval.value = setInterval(()=>{
      timer.value ++
    }, 1000)
  }
  else {
    clearInterval(interval.value)
  }
})
// hooks
onMounted(()=>{
  setTimeout(()=>{
    isDialing.value = false
  }, 2000)
})
</script>

