<script setup>
import Checkbox from "@/components/Base/Checkbox/Checkbox";
definePageMeta({
  layout: 'full'
})
const router = useRouter()
const users = useUsers()
const mainUser = ref(users.value.find(item=> item.id == 3))
const user = reactive({
  name: mainUser.value.title,
  username: mainUser.value.description.split(',')[0],
  grade: 'Lorem2'
})
const gradeOptions = ['Lorem1', 'Lorem2', 'Lorem3']
const updateUser = ()=> {
  let main = users.value.find(item=> item.id == 3)
  main.title = user.name
  main.description = `@${user.username}, grade: ${user.grade}, Montreal`
  router.push({ path: '/profile/settings' })
}
</script>

